DROP TABLE IF EXISTS alunos;

CREATE TABLE alunos (
    id INTEGER PRIMARY KEY,
    nome VARCHAR(255),
    curso_id INTEGER,
    idade INTEGER,
    cpf CHAR(14),
    genero VARCHAR(63),
    telefone VARCHAR(18),
    email VARCHAR(255)
);

